mapper.select:
select m.id, name, age, gender, tel, profileimg, pr, about_me, town
from member m join town t on m.id = t.id
where m.id = #{id}

select p.*, meetingname,operator,bannerimg from performance p join meeting m on m.meetingno = p.meetingno

	where town = #{town}

order by PERFORM_DATE desc

mapper.update:
UPDATE MEMBER
profileimg=#{img}, pr=#{pr}, about_me=#{about_me},
WHERE id=#{id}
