mapper.select:
SELECT * FROM(SELECT T.*, ROWNUM R FROM(
SELECT m.*, f.amount, fundingenddate FROM FUNDING f JOIN MEETING m ON f.meetingno = m.meetingno JOIN CATEGORY c ON m.categoryno=c.categoryno WHERE f.enabled='1'
AND m.town=#{town}
ORDER BY f.meetingno DESC)T) WHERE R BETWEEN #{start} AND #{end}

SELECT COUNT(*) FROM FUNDING f JOIN MEETING m ON f.meetingno = m.meetingno
WHERE f.enabled='1'

	AND m.town=#{town}
